/**
 * @internal
 * @abstract
 */
export default class MirrorChannel {
    /**
     * @abstract
     * @returns {void}
     */
    close(): void;
    /**
     * @abstract
     * @internal
     * @param {Uint8Array} requestData
     * @param {(error: number?, data: Uint8Array?) => void} callback
     * @returns {() => void}
     */
    makeServerStreamRequest(requestData: Uint8Array, callback: (error: number | null, data: Uint8Array | null) => void): () => void;
}
